import { useState } from "react";
import { Link } from "react-router-dom";

function Register(props) {
    const [formData, setFormData] = useState({
        email:'',
        password:''
    }) 

    function handleChange(evt) {
        const {name, value} = evt.target;
        setFormData({
            ...formData,
            [name]: value
        })
    }

    function handleSubmit(evt) {
        evt.preventDefault();
        props.handleRegistration(formData);
    }

    return(
        <div className="register">
            <p className="register__entry">Регистрация</p>
            <form className="register__form" onSubmit={handleSubmit}>
                <input name="email" type="email" id="email" placeholder="Email" className="register__input" value={formData.email} onChange={handleChange}></input>
                <input name="password" type="password" id="password" placeholder="Пароль" className="register__input" value={formData.password} onChange={handleChange}></input>
                <button className="register__button" type="submit" onSubmit={handleSubmit}>Зарегистрироваться</button>
            </form>
            <p className="register__quest">Уже зарегистрированы? <Link to="/sign-in" className="register__link">Войти</Link></p>
        </div>
    )
}

export default Register;
