import successLogo from '../images/success.svg'
import failureLogo from '../images/failure.svg'

function InfoTooltip(props) {
    return (
      <div
        className={`popup popup_tooltip ${
          props.isOpen ? "popup_active" : ""
        }`}
      >
        <div className="popup__content">
          <button
            type="button"
            aria-label="Закрыть"
            className={"popup__quit-button popup__close-tooltip"}
            onClick={props.onClose}
          ></button>
          {props.succesReg ? (<>
          <img className="popup__img" src={successLogo} />
          <h2 className="popup__title popup__title_center">Вы&nbsp;успешно зарегистрировались!</h2>
          </>) : (<>
          <img className="popup__img" src={failureLogo} />
          <h2 className="popup__title popup__title_center">Что-то пошло не&nbsp;так! Попробуйте еще раз.</h2>
          </>)}
          {/* <img className="popup__img" src={`${props.succesReg ? "../images/success.svg" : "../images/failure.svg"}`} />
          <h2 className="popup__title">{props.succesReg ? ("Вы успешно зарегистрировались!") : ("Что-то пошло не так! Попробуйте еще раз.")}</h2> */}
        </div>
      </div>
    );
  }
  
  export default InfoTooltip;
