function InfoTooltip(props) {
    return (
      <div
        className={`popup popup_tooltip ${
          props.isOpen ? "popup_active" : ""
        }`}
      >
        <div className="popup__content">
          <button
            type="button"
            aria-label="Закрыть"
            className={"popup__quit-button popup__close-tooltip"}
            onClick={props.onClose}
          ></button>
          <img className="popup__img" src={`${props.loggedIn ? "../images/success.svg" : "../images/failure.svg"}`} />
          <h2 className="popup__title">{props.loggedIn ? ("Вы успешно зарегистрировались!") : ("Что-то пошло не так! Попробуйте еще раз.")}</h2>
        </div>
      </div>
    );
  }
  
  export default InfoTooltip;
